import keras
import numpy as np

class PerClassMetric(keras.callbacks.Callback):

	def __init__(self, X_valid, Y_valid, batch_size):
		self.X_valid = X_valid
		self.Y_valid = Y_valid
		self.batch_size = batch_size
		self.index = self.get_indexlist()
		self.all_predictions = []
		self.all_recalls = []

	def on_epoch_end(self, epoch, logs=None):
		base_predictions = self.model.predict(x=self.X_valid, batch_size=self.batch_size, verbose=0)
		predictions = np.argmax(base_predictions, axis=-1)
		precisions = []
		recalls = []
		for class_index in range(len(self.index)):
			index_list = self.index[class_index]
			# compute precision
			counter_all = 0
			counter_true = 0
			for i in range(len(predictions)):
				if predictions[i] == class_index:
					counter_all += 1
					if i in index_list:
						counter_true += 1
			if counter_all == 0:
				precision = 0.0
			else:
				precision = counter_true * 1.0 / counter_all

			if len(index_list) == 0:
				recall = 0.0
			else:
				recall = counter_true * 1.0 / len(index_list)
			precisions.append(precision)
			recalls.append(recall)
		self.all_predictions.append(precisions)
		self.all_recalls.append(recalls)
		# print("precisions and recalls in this epoch:")
		# print(precisions)
		# print(recalls)




